import React from 'react';
import ReactDOM from 'react-dom';
import { Provider } from "react-redux";
import Game from './components/game'
import { createStore } from 'redux'
import calculateWinner from './helper/calculateWinner'

import './index.css';

const initialState = {
  history: [
    {
      squares: Array(9).fill(null)
    }
  ],
  stepNumber: 0,
  xIsNext: true
}

const reducer = (state = initialState, action )  => {
  switch(action.type)
  {
    case 'CLICK':
      {
        const i = action.i
        const history = this.props.state.history.slice(0, this.props.state.stepNumber + 1);
        const current = history[history.length - 1];
        const squares = current.squares.slice();
        if (calculateWinner(squares) || squares[i]) {
          return;
        }
        squares[i] = this.props.state.xIsNext ? "X" : "O";
        this.setState({
          history: history.concat([
            {
              squares: squares
            }
          ]),
          stepNumber: history.length,
          xIsNext: !this.props.state.xIsNext
        });
      }

  }
  return state
}

const store = createStore(reducer)
// ======================================== //

ReactDOM.render(<Provider store={store}><Game /></Provider>, document.getElementById("root"))
